import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function BookTicket(props) {
  const [id, setId] = useState([]);
  const [mobileNumber, setMobileNumber] = useState('');
  const [fullName, setfullName] = useState('');
  const [DOB, setDOB] = useState('');
  const [Address, setAddress] = useState('');
  const [Gender, setGender] = useState('');
  const [nationality, setnationality] = useState('');
  const [maritalStatus, setmaritalStatus] = useState('');
  const [passportNumber, setpassportNumber] = useState('');
  const [pIssueDate, setpIssueDate] = useState('');
  const [pExpiredDate, setpExpiredDate] = useState('');
  const [purptoseOfVisit,setpurptoseOfVisit] = useState('');
  const [VisaType, setVisaType] = useState('');
  const [DurationofStay, setDurationofStay] = useState('');

  // const [VisaDays, setVisaDays] = useState('');
  const [intArrivalDate, setintArrivalDate] = useState('');
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [selectedFile2, setSelectedFile2] = useState(null);
  const [letterOfInvi, setletterOfInvi] = useState (null);

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  // const handleVisaDaysChange = (event) => {
  //   setVisaDays(event.target.value);
  // };
    const handlepurptoseOfVisit = (event) => {
    setpurptoseOfVisit(event.target.value);
  };

 const handleVisaType = (event) => {
    setVisaType(event.target.value);
  };

   const handleDurationofStay = (event) => {
    setDurationofStay(event.target.value);
  };


  const handleMaritalStatusChange = (event) => {
    setmaritalStatus(event.target.value);
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleFileChange2 = (event) => {
    setSelectedFile2(event.target.files[0]);
  };

    const handleFileChange3 = (event) => {
    setletterOfInvi(event.target.files[0]);
  };

    const handleFileChange4 = (event) => {
    setStatement(event.target.files[0]);
  };


  const handleSubmit = (event) => {
    event.preventDefault();
    const bookingId = Math.floor(Math.random() * 1000000);

    props.setData([
      ...props.data,
      {
        id: props.data.length + 1,
        mobileNumber,
        fullName,
        DOB,
        Address,
        Gender,
        nationality,
        maritalStatus,
        passportNumber,
        pIssueDate,
        pExpiredDate,
        purptoseOfVisit,        
        // VisaDays,
        VisaType,
        intArrivalDate,
        DurationofStay,
        selectedSeats,
        passportPhoto: selectedFile ? selectedFile.name : 'No file uploaded',
        visaPhoto: selectedFile2 ? selectedFile2.name : 'No file uploaded',
        letterOfInvitation: letterOfInvi ? letterOfInvi.name : 'No file uploaded',

      },
    ]);

    alert(
      `Booking ID: ${bookingId}\nMobile Number: ${mobileNumber}\nFull Name: ${fullName}\nDOB: ${DOB}\nGender: ${Gender}\nNationality: ${nationality}\nMarital Status: ${maritalStatus}\nPassport Number: ${passportNumber}\nPassport Issue Date: ${pIssueDate}\nPassport Expired Date: ${pExpiredDate}\nArrival Date: ${intArrivalDate}\nSelected Seats: ${selectedSeats.join(', ')}\nUploaded File: ${selectedFile ? selectedFile.name : 'None'}`
    );
  };

  return (
    <form onSubmit={handleSubmit} className="book-ticket-form">
      <h2>Apply Visa</h2>

      <div className="form-group" style={{ margin: '1em' }}>
        <div style={{ marginTop: '2em' }}>
          <b>Personal Information</b>
        </div>

        <div className="form-group" style={{ marginTop: '1em' }}>
          <label>Full Name (as per passport):</label>
          <input type="text" value={fullName} onChange={(e) => setfullName(e.target.value)} required />

          <label>Mobile Number:</label>
          <input type="tel" value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value)} required />

          <label>DOB:</label>
          <input type="date" value={DOB} onChange={(e) => setDOB(e.target.value)} required />

          <label>Address (as per passport):</label>
          <input type="text" value={Address} onChange={(e) => setAddress(e.target.value)} required />


          <label>Gender:</label>
          <div className="form-group" style={{ marginLeft: '4em' }}>
            <label>
              <input type="radio" value="male" checked={Gender === 'male'} onChange={handleGenderChange} />
              Male
            </label>
            <label>
              <input type="radio" value="female" checked={Gender === 'female'} onChange={handleGenderChange} />
              Female
            </label>
            <label>
              <input type="radio" value="other" checked={Gender === 'other'} onChange={handleGenderChange} />
              Other
            </label>
          </div>

          <label>Nationality:</label>
          <input type="text" value={nationality} onChange={(e) => setnationality(e.target.value)} required />

          <label>Marital Status:</label>
          <select className="form-select" value={maritalStatus} onChange={handleMaritalStatusChange} required>
            <option value="" disabled>
              select
            </option>
            <option value="Single">Single</option>
            <option value="Married">Married</option>
          </select>

          <label>Upload Passport-size Photo:</label>
          <input type="file" onChange={handleFileChange} accept=".jpg,.jpeg,.png,.pdf" />
        </div>

        <div style={{ marginTop: '2em' }}>
          <b>Passport Details</b>
        </div>

        <div className="form-group" style={{ marginTop: '1em' }}>
          <label>Passport Number:</label>
          <input type="text" value={passportNumber} onChange={(e) => setpassportNumber(e.target.value)} required />

          <label>Passport Issued Date:</label>
          <input type="date" value={pIssueDate} onChange={(e) => setpIssueDate(e.target.value)} required />

          <label>Passport Expired Date:</label>
          <input type="date" value={pExpiredDate} onChange={(e) => setpExpiredDate(e.target.value)} required />

          <label>Upload Passport Copy (PDF or image):</label>
          <input type="file" onChange={handleFileChange2} accept=".jpg,.jpeg,.png,.pdf" />


        </div>

        <div style={{ marginTop: '2em' }}>
          <b>Travel Information</b>
        </div>

        <div className="form-group" style={{ marginTop: '1em' }}>

           <label>Purpose of Visit:</label>
          <select className="form-select" value={purptoseOfVisit} onChange={handlepurptoseOfVisit} required>
            <option value="" disabled>
              select
            </option>
            <option value="Tourism">Tourism</option>
            <option value="Business">Business</option>
            <option value="Study">Study</option>
            <option value="Work">Work</option>

          </select>

          <label>Visa Type:</label>
          <select className="form-select" value={VisaType} onChange={handleVisaType} required>
            <option value="" disabled>
              select
            </option>
            <option value="Single_Entry">Single Entry</option>
            <option value="Multiple_Entry">Multiple Entry</option>

          </select>

          <label>Intended Arrival Date:</label>
          <input type="date" value={intArrivalDate} onChange={(e) => setintArrivalDate(e.target.value)} required />

           <label>Intended duration of stay:</label>
          <select className="form-select" value={DurationofStay} onChange={handleDurationofStay} required>
            <option value="" disabled>
              select
            </option>
            <option value="15 Days">15 Days</option>
            <option value="30 Days">30 Days</option>
            <option value="90 Days">90 Days</option>

          </select>

        </div>


          <div style={{ marginTop: '2em' }}>
          <b>Document Uploads</b>
        </div>

        <div className="form-group" style={{ marginTop: '1em' }}>

          <label>Letter of Invitation (PDF or image):</label>
          <input type="file" onChange={handleFileChange3} accept=".jpg,.jpeg,.png,.pdf" />

         <label>Bank Statement or Proof of Funds (PDF or image):</label>
          <input type="file" onChange={handleFileChange4} accept=".jpg,.jpeg,.png,.pdf" />


        </div>

      </div>

      <button type="submit">Apply For Visa</button>
    </form>
  );
}
