import React, {useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';


export default function BookTicket(props
    // {date, id, setId, setDate, mobileNumber, setMobileNumber, selectedSeats, setSelectedSeats}
    ) {
     const [date, setDate] = useState('');
    const [id, setId] = useState([]);
    const [mobileNumber, setMobileNumber] = useState('');
    const [fullName, setfullName] = useState('');
    const [DOB, setDOB] = useState('');
    const [Gender, setGender] = useState('');
    const [nationality, setnationality] = useState('');
    const [passportNumber,setpassportNumber] = useState('');
    const [pIssueDate,setpIssueDate] = useState('');
    const [pExpiredDate,setpExpiredDate] = useState('');
    const [VisaDays, setVisaDays] = useState('');
    const [intArrivalDate, setintArrivalDate] = useState('');
    const [selectedSeats, setSelectedSeats] = useState([]);

    const handleChange = (event) => {
    setGender(event.target.value);
    };

      const handleChange2 = (event) => {
    setVisaDays(event.target.value);
  };

    const handleSeatSelect = (seat) => {
        if (selectedSeats.length >= 6) {
            alert('Maximum number of seats reached for this date');
            return;
        }
        if (selectedSeats.includes(seat)) {
            setSelectedSeats(selectedSeats.filter((s) => s !== seat));
        } else {
            setSelectedSeats([...selectedSeats, seat]);
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const bookingId = Math.floor(Math.random() * 1000000);
        props.setData([
            ...props.data,
            {
               date: date,
                id: props.data.length+1,
                mobileNumber: mobileNumber,
                fullName: fullName,
                DOB: DOB,
                Gender:Gender,
                nationality: nationality,
                passportNumber:passportNumber,
                pIssueDate:pIssueDate,
                pExpiredDate:pExpiredDate,
                VisaDays: VisaDays,
                intArrivalDate: intArrivalDate,
                selectedSeats: selectedSeats
            }
        ])
        alert(`Booking ID: ${bookingId}\nDate: ${date}\nMobile Number: ${mobileNumber}\nFull Name: ${fullName}\nDOB: ${DOB}\nGender: ${Gender}\nNationality: ${nationality}\nPassportNumber: ${passportNumber}\nPassport Issue Date: ${pIssueDate}\nPassport Expired Date: ${pExpiredDate}\nVisaDays: ${VisaDays}\nSelected Seats: ${selectedSeats.join(', ')}`);
    };

     return (
        <form onSubmit={handleSubmit} className="book-ticket-form" style={{"marginTop": "6em"}}>
            <h2 >Apply Visa</h2>
            <div className="form-group"  >
                <label htmlFor="date-input">Date:</label>
                <input type="date" id="date-input" value={date} onChange={(event) => setDate(event.target.value)} required />
            </div>
            <div className="form-group" style={{"margin": "1em"}}>

                <label htmlFor="name-input">Full Name:</label>
                <input type="tel" id="name-input" value={fullName} onChange={(event) => setfullName(event.target.value)} required />

                <label htmlFor="mobile-input">Mobile Number</label>
                <input type="tel" id="mobile-input" value={mobileNumber} onChange={(event) => setMobileNumber(event.target.value)} required />

                <label htmlFor="Dob-input">DOB:</label>
                <input type="date" id="Dob-input" value={DOB} onChange={(event) => setDOB(event.target.value)} required />

                <label htmlFor="Gender-input">Gender</label>

                <div className='form-group' style={{"marginLeft": "4em"}} >
      <label>
        <input
          type="radio"
          value="male"
          checked={Gender === 'male'}
          onChange={handleChange}
        />
        Male
      </label>

      <label>
        <input
          type="radio"
          value="female"
          checked={Gender === 'female'}
          onChange={handleChange}
        />
        Female
      </label>
      <label>
        <input
          type="radio"
          value="other"
          checked={Gender === 'other'}
          onChange={handleChange}
        />
        Other
      </label>


    </div>

    <label htmlFor="nation-input">Nationality:</label>
    <input type="text" id="nation-input" value={nationality} onChange={(event) => setnationality(event.target.value)} required />


    <label htmlFor="passportNumber-input">Passport Number:</label>
    <input type="text" id="passportNumber-input" value={passportNumber} onChange={(event) => setpassportNumber(event.target.value)} required />

    <label htmlFor="pIssueDate-input">Passport Issued Date:</label>
    <input type="date" id="pIssueDate-input" value={pIssueDate} onChange={(event) => setpIssueDate(event.target.value)} required />

    <label htmlFor="pExpiredDate-input">Passport Expired Date:</label>
    <input type="date" id="pExpiredDate-input" value={pExpiredDate} onChange={(event) => setpExpiredDate(event.target.value)} required />

    <div style={{"marginTop" : "2em"}}>
        <b>  Travel information </b>

    </div>

    <div className='form-group' style={{"marginTop" : "1em"}}>

   <label className='form-label' >
      Visa Required Days
        <select class="form-select" style={{"marginLeft" : "20px"}} value={VisaDays} onChange={handleChange2} required>
          <option value="" disabled>select</option>    
          <option value="30">30</option>

        </select>
    </label>

    </div>

    <label htmlFor="intArrivalDate-input">Intended Arrival Date:</label>
    <input type="date" id="intArrivalDate-input" value={intArrivalDate} onChange={(event) => setintArrivalDate(event.target.value)} required />          



            </div>
            <div className="form-group">
                <label>Seat Selection:</label>
                <div className="seat-selection">
                    {[...Array(20)].map((_, index) => {
                        const seat = `A${index + 1}`;
                        const isAvailable = selectedSeats.length < 6 && !selectedSeats.includes(seat);
                        const isSelected = selectedSeats.includes(seat);
                        const className = `seat ${isAvailable ? 'available' : 'unavailable'} ${isSelected ? 'selected' : ''}`;
                        return (
                            <div key={seat} className={className} onClick={() => handleSeatSelect(seat)}>
                                {seat}
                            </div>
                        );
                    })}
                </div>
            </div>
            <button type="submit">Book Ticket</button>
        </form>
    )
}
