        <input type="date" name="pExpiredDate" className="form-control" value={form.pExpiredDate} onChange={e => setForm({ ...form, pExpiredDate: e.target.value })} required />

        {/* <label>Passport Copy:</label>
        <input type="file" className="form-control" onChange={handleFileChange(setSelectedFile2)} accept=".jpg,.jpeg,.png,.pdf" /> */}

        <h5 style={{ marginTop: "2em" }}><b>Travel Information</b></h5>

        <label>Purpose of Visit:</label>
        <select name="purptoseOfVisit" className="form-control" value={form.purptoseOfVisit} onChange={e => setForm({ ...form, purptoseOfVisit: e.target.value })} required>
          <option value="" disabled>Select</option>
          <option value="Tourism">Tourism</option>
          <option value="Business">Business</option>
        </select>

        <label>Visa Type:</label>
        <input type="text" name="VisaType" className="form-control" value={form.VisaType} onChange={e => setForm({ ...form, VisaType: e.target.value })} required />

        <label>Duration of Stay:</label>
        <input type="text" name="DurationofStay" className="form-control" value={form.DurationofStay} onChange={e => setForm({ ...form, DurationofStay: e.target.value })} required />

        <label>Arrival Date:</label>
        <input type="date" name="intArrivalDate" className="form-control" value={form.intArrivalDate} onChange={e => setForm({ ...form, intArrivalDate: e.target.value })} required />

        {/* <label>Letter of Invitation:</label>
        <input type="file" className="form-control" onChange={handleFileChange(setLetterOfInvi)} accept=".jpg,.jpeg,.png,.pdf" />

        <label>Bank Statement:</label>
        <input type="file" className="form-control" onChange={handleFileChange(setStatement)} accept=".jpg,.jpeg,.png,.pdf" /> */}

        <label>Applicant Name:</label>
        <input type="text" name="applicantName" className="form-control" value={form.applicantName} onChange={e => setForm({ ...form, applicantName: e.target.value })} required />

        <label>Signature Full Name:</label>
        <input type="text" name="SignfullName" className="form-control" value={form.SignfullName} onChange={e => setForm({ ...form, SignfullName: e.target.value })} required />

        <button type="submit" className="btn btn-primary mt-4 mb-3">Submit Application</button>
      </div>
    </form>
  );
}
