import React, { useState } from "react";
// import 'bootstrap/dist/css/bootstrap.min.css';





export default function BookTicket(props) {
  const [id, setId] = useState([]);
  const [mobileNumber, setMobileNumber] = useState("");
  const [fullName, setfullName] = useState("");
  const [DOB, setDOB] = useState("");
  const [Address, setAddress] = useState("");
  const [Gender, setGender] = useState("");
  const [nationality, setnationality] = useState("");
  const [maritalStatus, setmaritalStatus] = useState("");
  const [passportNumber, setpassportNumber] = useState("");
  const [pIssueDate, setpIssueDate] = useState("");
  const [pExpiredDate, setpExpiredDate] = useState("");
  const [purptoseOfVisit, setpurptoseOfVisit] = useState("");
  const [VisaType, setVisaType] = useState("");
  const [DurationofStay, setDurationofStay] = useState("");

  // const [VisaDays, setVisaDays] = useState('');
  const [intArrivalDate, setintArrivalDate] = useState("");
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [selectedFile2, setSelectedFile2] = useState(null);
  const [letterOfInvi, setletterOfInvi] = useState(null);
  const [Statement, setStatement] = useState("null");
  const [SignfullName, setSignfullName] = useState("");
  const [applicantName, setapplicantName] = useState("");

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  // const handleVisaDaysChange = (event) => {
  //   setVisaDays(event.target.value);
  // };
  const handlepurptoseOfVisit = (event) => {
    setpurptoseOfVisit(event.target.value);
  };

  const handleVisaType = (event) => {
    setVisaType(event.target.value);
  };

  const handleDurationofStay = (event) => {
    setDurationofStay(event.target.value);
  };

  const handleMaritalStatusChange = (event) => {
    setmaritalStatus(event.target.value);
  };

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleFileChange2 = (event) => {
    setSelectedFile2(event.target.files[0]);
  };

  const handleFileChange3 = (event) => {
    setletterOfInvi(event.target.files[0]);
  };

  const handleFileChange4 = (event) => {
    setStatement(event.target.files[0]);
  };



  const handleSubmit = (event) => {
    event.preventDefault();
    const bookingId = Math.floor(Math.random() * 1000000);

  const handleSubmit = () =>{

  };


    props.setData([
      ...props.data,
      {
        id: props.data.length + 1,
        mobileNumber,
        fullName,
        DOB,
        Address,
        Gender,
        nationality,
        maritalStatus,
        passportNumber,
        pIssueDate,
        pExpiredDate,
        purptoseOfVisit,
        VisaType,
        intArrivalDate,
        DurationofStay, 
        passportPhoto: selectedFile ? selectedFile.name : "No file uploaded",
        visaPhoto: selectedFile2 ? selectedFile2.name : "No file uploaded",
        letterOfInvitation: letterOfInvi
          ? letterOfInvi.name
          : "No file uploaded",
        Statement: Statement ? Statement.name : "No file uploaded",
        applicantName,
        SignfullName,

      },
    ]);

    alert(
      `Booking ID: ${bookingId}\nMobile Number: ${mobileNumber}\nFull Name: ${fullName}\nDOB: ${DOB}\nGender: ${Gender}\nNationality: ${nationality}\nMarital Status: ${maritalStatus}\nPassport Number: ${passportNumber}\nPassport Issue Date: ${pIssueDate}\nPassport Expired Date: ${pExpiredDate}\nArrival Date: ${intArrivalDate}\nSelected Seats: ${selectedSeats.join(
        ", "
      )}\nUploaded File: ${selectedFile ? selectedFile.name : "None"}`
    );
  };

  return (
    <form onSubmit={handleSubmit} className="book-ticket-form ">

      <h1 className="mb-3">Apply Visa</h1>

      <div className="form-group" style={{ margin: "1em" }}>
        <div style={{ marginTop: "2em" }}>
          <h5 className="mb-3">
            {" "}
            <b>Personal Information</b>{" "}
          </h5>
        </div>

        <div className="form-group " style={{ marginTop: "1em" }}>
          <label>Full Name (as per passport): <span className="text-danger">*</span></label>
          <input
            type="text"
            class="form-control" 
            value={UserData.fullName}
            onChange={handleSubmit}
            required
          />

          <label>Mobile Number: <span className="text-danger">*</span> </label>
          <input
            type="tel"
             className="form-control" 
            value={UserData.mobileNumber}
            onChange={handleSubmit}
            required
          />

          <label>DOB: <span className="text-danger">*</span></label>
          <input
            type="date"
             class="form-control" 
            value={UserData.DOB}
            onChange={handleSubmit}
            required
          />

          <label>Address (as per passport): <span className="text-danger">*</span></label>
          <input
            type="text"
             class="form-control" 
            value={UserData.Address}
            onChange={handleSubmit}
            required
          />

          <div className="mb-3">
            <label className="form-label ">Gender: <span className="text-danger">*</span></label>
            <div className="ms-4">
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="gender"
                  id="genderMale"
                  value={UserData.male}
                  checked={Gender === "male"}
                  onChange={handleGenderChange}
                />
                <label className="form-check-label" htmlFor="genderMale">
                  Male
                </label>
              </div>

              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="gender"
                  id="genderFemale"
                  value={UserData.female}
                  checked={Gender === "female"}
                  onChange={handleGenderChange}
                />
                <label className="form-check-label" htmlFor="genderFemale">
                  Female
                </label>
              </div>

              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="gender"
                  id="genderOther"
                  value={UserData.other}
                  checked={Gender === "other"}
                  onChange={handleGenderChange}
                />
                <label className="form-check-label" htmlFor="genderOther">
                  Other
                </label>
              </div>
            </div>
          </div>

          <label>Nationality: <span className="text-danger">*</span></label>
          <input
            type="text"
             class="form-control" 
            value={UserData.nationality}
            onChange={handleSubmit}
            required
          />

          <label>Marital Status: <span className="text-danger">*</span></label>
          <select
            className="form-select"
             class="form-control" 
            value={UserData.maritalStatus}
            onChange={handleMaritalStatusChange}
            required
          >
            <option value="" disabled>
              select
            </option>
            <option value="Single">Single</option>
            <option value="Married">Married</option>
          </select>

          <label>Upload Passport-size Photo: <span className="text-danger">*</span></label>
          <input
            type="file"
             class="form-control" 
            onChange={handleFileChange}
            accept=".jpg,.jpeg,.png,.pdf"
            required
          />
        </div>

        <div style={{ marginTop: "2em" }}>
          <h5 className="mb-3">
            {" "}
            <b>Passport Details</b>{" "}
          </h5>
        </div>

        <div className="form-group" style={{ marginTop: "1em" }}>
          <label>Passport Number: <span className="text-danger">*</span></label>
          <input
            type="text"
             class="form-control" 
            value={UserData.passportNumber}
            onChange={handleSubmit}
            required
          />

          <label>Passport Issued Date: <span className="text-danger">*</span></label>
          <input
            type="date"
             class="form-control" 
            value={UserData.pIssueDate}
            onChange={handleSubmit}
            required
          />

          <label>Passport Expired Date: <span className="text-danger">*</span> </label>
          <input
            type="date"
             class="form-control" 
            value={UserData.pExpiredDate}
            onChange={handleSubmit}
            required
          />

          <label>Upload Passport Copy (PDF or image): <span className="text-danger">*</span> </label>
          <input
            type="file"
             class="form-control" 
            onChange={handleFileChange2}
            accept=".jpg,.jpeg,.png,.pdf"
          />
        </div>

        <div style={{ marginTop: "2em" }}>
          <h5 className="mb-3">
            <b> Travel Information</b>{" "}
          </h5>
        </div>

        <div className="form-group" style={{ marginTop: "1em" }}>
          <label>Purpose of Visit: <span className="text-danger">*</span></label>
          <select
            className="form-select"
            value={UserData.purptoseOfVisit}
            onChange={handlepurptoseOfVisit}
            required
          >
            <option value="" disabled>
              select
            </option>
            <option value="Tourism">Tourism</option>
            <option value="Business">Business</option>
            <option value="Study">Study</option>
            <option value="Work">Work</option>
          </select>

          <label>Visa Type: <span className="text-danger">*</span></label>
          <select
            className="form-select"
             class="form-control" 
            value={UserData.VisaType}
            onChange={handleVisaType}
            required
          >
            <option value="" disabled>
              select
            </option>
            <option value="Single_Entry">Single Entry</option>
            <option value="Multiple_Entry">Multiple Entry</option>
          </select>

          <label>Intended Arrival Date: <span className="text-danger">*</span> </label>
          <input
            type="date"
            value={UserData.intArrivalDate}
            onChange={handleSubmit}
            required
          />

          <label>Intended duration of stay: <span className="text-danger">*</span> </label>
          <select
            className="form-select"
            value={UserData.DurationofStay}
            onChange={handleDurationofStay}
            required
          >
            <option value="" disabled>
              select
            </option>
            <option value="15 Days">15 Days</option>
            <option value="30 Days">30 Days</option>
            <option value="90 Days">90 Days</option>
          </select>
        </div>

        <div style={{ marginTop: "2em" }}>
          <h5 className="mb-3">
            {" "}
            <b>Document Uploads</b>{" "}
          </h5>
        </div>

        <div className="form-group" style={{ marginTop: "1em" }}>
          <label>Letter of Invitation (PDF or image): <span className="text-danger">*</span> </label>
          <input
            type="file"
            onChange={handleFileChange3}
            accept=".jpg,.jpeg,.png,.pdf"
          />

          <label>Bank Statement or Proof of Funds (PDF or image): <span className="text-danger">*</span> </label>
          <input
            type="file"
            onChange={handleFileChange4}
            accept=".jpg,.jpeg,.png,.pdf"
          />
        </div>

        {/* <div style={{ marginTop: '2em' }}>
          <b>Declaration & Signature</b>
        </div> */}

        <div className="form-group mt-4">
          <h5 className="mb-3">
            <b>Declaration & Signature</b>
          </h5>

          <div className="form-check mb-3">
            <input
              className="form-check-input"
              type="checkbox"
              id="declarationCheck"
              required
            />
            <label className="form-check-label" htmlFor="declarationCheck">
              I hereby declare that the information provided above is true and
              correct to the best of my knowledge. I understand that any false
              information may lead to the rejection of my visa application.
            </label>
          </div>

          <div className="row mb-3">
            <div className="col-md-6">

              <label>Applicant's Full Name:  <span className="text-danger">*</span></label>
          <input
            type="text"
            class="form-control" 

            value={UserData.applicantName}
            onChange={handleSubmit}
            required
          />

            </div>

            <div className="col-md-6">
              <label htmlFor="signatureDate" className="form-label">
                Date
              </label>
              <input
                type="date"
                className="form-control"
                id="signatureDate"
                value={new Date().toISOString().split("T")[0]}
                readOnly
              />
            </div>
          </div>

          <div className="mb-3">
            <label className="form-label">Signature (Type Full Name)  <span className="text-danger">*</span></label>
            <input
              type="text"
              className="form-control"
              value={UserData.SignfullName}
              onChange={handleSubmit}
              required
            />
          </div>
        </div>
      </div>

      <button type="submit">Apply For Visa</button>
    </form>
  );
}
